/**
 * This is script loads the individual files that make up geoweb.min.js, used
 * during development when running from within the source tree.
 */

/**
 * Get the script element we are in.
 */
function self() {
  var scripts = document.getElementsByTagName("script");

  return scripts[scripts.length - 1];
}

/**
 * Import a JavaScript source file into the current document.s
 */
function importScript(src) {
  document.write('<script src="' + src + '" ></script>')
}

